import React, { useEffect, useState } from 'react';
import axios from 'axios';

function App() {
  const [currencyPairs, setCurrencyPairs] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchCurrencyPairs = async () => {
      try {
        const response = await axios.get('http://localhost:8000/api/currency_pairs/');
        setCurrencyPairs(response.data);
      } catch (error) {
        setError(error.message);
      }
    };

    fetchCurrencyPairs();
  }, []);

  return (
    <div>
      <h1>Currency Pairs</h1>
      {error && <p>Error fetching currency pairs: {error}</p>}
      {currencyPairs && (
        <ul>
          {Object.entries(currencyPairs).map(([currency, rate]) => (
            <li key={currency}>
              {currency}: {rate}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default App;
