import React, { useEffect, useState } from 'react';
import axios from 'axios';

function App() {
  const [usdtPairs, setUsdtPairs] = useState([]);
  const [otherPairs, setOtherPairs] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchCurrencyPairs = async () => {
      try {
        const response = await axios.get('http://localhost:8000/api/currency_pairs/');
        const currencyPairs = response.data.currency_pairs;

        // Filter currency pairs based on currency code
        const usdtPairs = currencyPairs.filter(pair => pair.currency === 'USDT');
        const otherPairs = currencyPairs.filter(pair => pair.currency !== 'USDT');

        // Calculate equivalent USDT for other currencies
        const usdtEquivalentPairs = otherPairs.map(pair => {
          const usdtPair = usdtPairs.find(usdt => usdt.currency === 'USDT');
          const rateUsdt = parseFloat(usdtPair.rate);
          const rateOther = parseFloat(pair.rate);
          const rateEquivalent = rateOther / rateUsdt;
          return { currency: pair.currency, rate: rateEquivalent.toFixed(8) };
        });

        setUsdtPairs(usdtPairs);
        setOtherPairs(usdtEquivalentPairs);
      } catch (error) {
        setError(error.message);
      }
    };

    fetchCurrencyPairs();
  }, []);

  return (
    <div>
      <h1>Currency Pairs</h1>
      {error && <p>Error fetching currency pairs: {error}</p>}
      <div style={{ display: 'flex' }}>
        <div style={{ flex: 1 }}>
          <h2>USDT Pairs</h2>
          <ul>
            {usdtPairs.map(pair => (
              <li key={pair.currency}>USDT/{pair.currency}: {pair.rate}</li>
            ))}
          </ul>
        </div>
        <div style={{ flex: 1 }}>
          <h2>Equivalent USDT</h2>
          <ul>
            {otherPairs.map(pair => (
              <li key={pair.currency}>{pair.currency}: {pair.rate} USDT</li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}

export default App;
