import React, { useEffect, useState } from 'react';
import axios from 'axios';

function App() {
  const [currencyPairs, setCurrencyPairs] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchCurrencyPairs = async () => {
      try {
        const response = await axios.get('http://localhost:8000/api/currency_pairs/');
        console.log('Response:', response.data);
        setCurrencyPairs(response.data.currency_pairs); // Accessing currency_pairs key
      } catch (error) {
        console.error('Error fetching currency pairs:', error.message);
        setError(error.message);
      }
    };

    fetchCurrencyPairs();
  }, []);


  return (
    <div>
      <h1>Currency Pairs</h1>
      {error && <p>Error fetching currency pairs: {error}</p>}
      {currencyPairs && currencyPairs.length > 0 ? (
        <ul>
          {currencyPairs.map(({ currency, rate }) => (
            <li key={currency}>
              {currency}: {rate}
            </li>
          ))}
        </ul>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
}

export default App;
